package loops;

public class Loop5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int k= 1;
		for(int i=1;i<5;i++)
		{
			for(int j=1;j<=i;j++) {
			
		       System.out.print(3*k);
			   System.out.print("\t");
			   k++;
		  
		}
			
			 System.out.println("");

	}
	}

}
